import { Player } from "../../player.js";
import { ByteStream } from "../../bytestream.js";
import { Config } from "../../config.js";
import { BattleEndData } from "../../battleenddata.js";

export class BattleEndMessage {
    static encode(player: Player, data: BattleEndData): number[] {
        let stream = new ByteStream([]);

        stream.writeHexa("887E09BD83B1406CA167B86598AB1265");
        stream.writeVint(2);
        stream.writeVint(1);
        stream.writeVint(0);
        stream.writeVint(1488);
        stream.writeVint(0);
        stream.writeVint(0);
        stream.writeHexa("820A0000000000000000000005");
        stream.writeHexa("1E000000107F000101011000011D1D01AF020105010000");
        stream.writeVint(1);
        stream.writeVlong(0, 1);
        stream.writeHexa("000000");
        stream.writeHexa("");

        return stream.payload;
    }
}